import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import { Card, Col, Container, Row } from 'reactstrap'
// import Navbar from './Navbar'

export const DeleteProduct = () => {

    const [product, setProducts] = useState([])

    useEffect(() => {

        getAllProducts();
    }, [])

    const getAllProducts = () => {
        fetch("http://localhost:9091/admin/washpack").then((result) => {

            result.json().then((resp) => {

                setProducts(resp);

            });

        });
    }



    const deleteProduct = (productId) => {
        axios.delete('http://localhost:9091/admin/deletepack' + productId)
            .then((response) => {
                getAllProducts();

            }).catch(error => {
                console.log(error);
            })

    }

    return (


        <div>
            {/* <Navbar /> */}
            <Container fluid>
                <span>
                    <h3>Products</h3>
                </span>

                <Row>
                    {product.map((p, index) => (
                        <Col sm="4">
                            <div className="card w-55 shadow-lg  mb-5 bg-white rounded" style={{ width: 400 }}>
                                <img className="card-img mx-auto" src={p.imageUrl} alt="Card image" style={{ width: 220, height: 220 }} />
                                <div className="card-body text-center">
                                    <div className="details">
                                        {/* <p>{p.couponId}</p> */}
                                        <h3>{p.id}</h3>
                                        <p>Price - {p.cost}Rs</p>

                                        <Link className="btn btn-info" to={`/updateProduct/${product.id}`} >Update</Link>
                                        <button className="btn btn-danger" onClick={() => deleteProduct(p.id)}
                                            style={{ marginLeft: "10px" }}> Delete</button>
                                    </div>
                                </div>
                            </div>
                        </Col>
                    ))}
                </Row>

            </Container>
        </div>

    )
}
