import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";

toast.configure();
const Booking = () => {
  // Start
  const [washers, setWashers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:9093/user/washpack").then((result) => {
      result.json().then((resp) => {
        setWashers(resp);
        console.log(resp);
      });
    });
  }, []);


  let navigate = useNavigate();

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    washpackId: "",
    address: "",
  });

  const changeHandler = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const submitHandler = () => {
    axios
      .post("http://localhost:9092/order/addorder", order)
      .then((res) => {
        if (res.status === 200)

         alert("Booked Successfully");

        else Promise.reject();
      })
      .catch((err) => alert("Something went wrong"));
  };
  const paymentHandler = () => {
    navigate("/Payment");
  }

  return (
    <>
      {/* <div className="formTop"> */}
        {/* <div className="formTitle"> */}
          <p className="c1 text-center">
            <h3>BOOK YOUR CAR WASH </h3>
          </p>
        {/* </div> */}
      {/* </div> */}
      <div>
        <div className="formDetails">
          <form className="actualForm" onSubmit={submitHandler}>
            <div style={{ marginBottom: "30px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>


            {washers.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={changeHandler} /> {emp.cost}
              </div>
            ))}

            <div style={{ marginBottom: "30px" }}>
              <p className="formName">CONTACT DETAILS</p>
            </div>
            <div>
              <input
                className="contactDetails"
                type="text"
                name="username"
                onChange={changeHandler}
                placeholder="Enter Your Full Name"
              />

              <input
                className="contactDetails"
                name="carName"
                type="text"
                placeholder="Enter Your Car Name"
                onChange={changeHandler}
              />


              <input
                className="contactDetails"
                type="text"
                name="carModel"
                placeholder="Enter Your Car Model"
                onChange={changeHandler}
              />



              <input 
              className="formDateTime"
              type="date"
               name="date" 
               onChange={changeHandler} />



              <input
                type="text"
                className="contactDetails"
                name="contactno"
                onChange={changeHandler}
                placeholder="Enter Contact no"
              />

              <input
                className="contactDetails"
                name="washpack"
                type="number"
                placeholder="Enter Your Washpack Id"
                onChange={changeHandler}
              />


              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="5"
                type="text"
                name="address"
                onChange={changeHandler}
                placeholder="Your Address"
              />
            </div>

            <button onClick={submitHandler} className="btn btn-success mr-2" style={{ fontSize: "15px" }}>Submit</button>
          </form>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
};
export default Booking;



















