import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Row, Table } from "react-bootstrap";
import Footer from "./Footer";
import {card} from "react-bootstrap"
import { CardBody, CardLink, CardSubtitle, CardText, CardTitle } from "reactstrap";
import AdminNavbar from "./AdminNab";


export default class Profile extends Component { 
  
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }

  
  componentDidMount() {
    fetch("http://localhost:9093/user/allusers")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        });
      });
  }

  //End

  render()
   {
    const { DataisLoaded, items } = this.state;

    return (
      
      <>
      
     
      <AdminNavbar />
        <div className="text-centre">
          <h3 style={{marginLeft:"550px"}}>
            User Profiles
          </h3>
 
        </div>

        <Row xs={1} md={4} className="g-4 col-md-7"style={{marginTop:"50px"}}> 
        {items.map((item) => (
          <div className="container">
            <div className="row ">
              <div className="card" style={{width:"250px",marginBottom:"40px"}}>
                <div className="card-header text-centre">
                  
                <label><b>{item.name}</b></label>
                

                </div>
                <div className="card-body"  >
                <label>Id: {item.id}</label><br/>
                <label>UserName: {item.username}</label><br/>
                <label>Contact: {item.contactno}</label><br/>
                <label>Email: {item.email}</label><br/>
                

                </div>

              </div>



            </div>

           
            
          </div>
          
        ))}
        
        
        </Row>
        <div>
          <Footer />
        </div>
        
      </>
    );
  

 }
}
