import React from 'react';
import './About.css';
import Footer from '../Footer';



const About = () => {
  return (
    <div className='row col-md-9 '>
    
      
      <div className='aboutus col-md-6 ' style={{marginleft:"20px"}} >
        <h1>ABOUT US</h1><br></br>
       
        <p style={{marginleft:"20px"}}>GREEN WASH CAR WASH is a brand which is now going to change the thinking of people about car cleaning. GREEN WASH CAR WASH is an eco-friendly 
          car wash company that provides all the car care solution, we operate in multiple cities through our partner network of 281 service stations in
         arround 16 cities in India. Our company started working in 2017 by a team of experts with more then 10 years of Professional Car Washing .
         Our goal is to provide our customers with convenient Car Washing  blended with the expertised equipments and BEST-IN-CLASS TECHNOLOGY. 
          we use the most modern and up-to-date water reclaimation modules as a part of our car wash 
          systems. Our products are all biodegradable and eco-friendly.  Our highly skilled professionals are trained for your car's care, We have the best team for your car's INTERIOR & EXTERIOR WASH as well.</p>
      </div>
      <div className='hi col-md-3'>


      </div>
      
      
      <Footer />
    </div>
    



  );
};

export default About;
