import React, { Component } from "react";
import PaymentService from "./PaymentService";
import { toast } from 'react-toastify';
// import { ToastContainer } from 'react-toastify';
//     import 'react-toastify/dist/ReactToastify.css';
//     toast.configure()

//     /**To Show Add Toastify Text */

//     const notify = () => {

//       toast.success('Payment Done Successfully!!',
//         {
//           position: "top-center",
//           autoClose: 3000
//         });



    // }


export default class PaymentOpen extends Component {
  state = {
    amount: "",
  };

  onHandleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value,
    });
  };

  componentDidMount() {
    const script = document.createElement("script");
    script.src = "https://checkout.razorpay.com/v1/checkout.js";
    script.async = true;
    document.body.appendChild(script);
  }

  onSubmit = (e) => {
    e.preventDefault();
    let orderDetail = { amount: this.state.amount };

    PaymentService.createOrder(orderDetail).then((res) => {
      console.log("PAYMENT ORDER RESPONSE", res);
      var options = {
        key: "rzp_test_0WdhmJkGlTdoLb", 
        amount: res.data.amount,
        currency: "INR",
        name: "Green Wash Car Wash",

        order_id: res.data.id,
        handler: function (response) {
          // console.log(response);
          alert("Payment Successfull !!!");
          if (response && response.data) {
            toast.success('Order Placed Proceeding to payment '  ,
            {
              position: "top-center",
              autoClose: 3000
            });
          }
  //             toast.success('Payment Done Successfully',{

  //               position: "top-center",
  //                      autoClose: 3000

  //  });
          // notify();
          window.location.href = "/Userhome";
        }
        ,
        prefill: {
          name: "",
          email: "",
          contact: "",
        },
        notes: {
          address: "Green Wash Car Wash",
        },
        theme: {
          color: "#26a69a",
        },
      };
      var rzp1 = new window.Razorpay(options);
      rzp1.open();
    });
  };

  render() {
    return (
      <div>
        <form
          style={{
            backgroundColor: "#488AC7",
            width: "250px",
            height: "340px",
            marginLeft: "150px",
            marginTop: "15px",
          }}
        >
          <br />
          <br />
          <div className="form-outline mb-4">
            <label className="form-label" htmlFor="form2Example1">
              <b>Enter Amount</b>
            </label>
            <input
              type="text"
              id="form2Example1"
              className="form-control"
              placeholder="In Rupees"
              name="amount"
              value={this.state.amount}
              onChange={this.onHandleChange}
            />
          </div>
          <button
            type="button"
            className="btn btn-success btn-block mb-4"
            onClick={this.onSubmit}
          >
            Procced To Pay
          </button>
        </form>
      </div>
    );
  }
}
