<div className="text-centre">
          <h3 style={{marginLeft:"550px"}}>
            Washer Profiles
          </h3>


        </div>
        {items.map((item) => (
          <div className="container">
            <div className="row ">
              <div className="card" style={{width:"250px",marginBottom:"40px"}}>
                <div className="card-header text-centre">
                <label><b>{item.id}</b></label>


                </div>
                <div className="card-body"  >
                <label>Id: {item.id}</label><br/>
                <label>Name: {item.name}</label><br/>
                <label>Location: {item.location}</label><br/>
                <td><Link to ={`/Washer/${item.id}`}className="btn btn-outline-info">Delete Washer</Link></td>



                </div>

              </div>



            </div>



          </div>
        ))}

        <div>
          <Footer />
        </div>
      </>
    );
  }
}
