import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams, } from 'react-router';
import { Link } from 'react-router-dom';
// import { baseUrl } from '../util/AppConstants';
// import NavBarAdmin from './NavBarAdmin';
function UpdatePack() {
    const [pack, setPack] = useState({
      cost: "",
        packname: "",
         description: ""
         

    });
    const { id } = useParams();

    const navigate = useNavigate();
    const fetchPackById = () => {
        axios.get("http://localhost:9091/admin/viewPack/"+id)
        .then(resp => setPack(resp.data))
    }
    useEffect(fetchPackById, []);
    const handleChange = (event) => {
        setPack(pack => ({ ...pack, [event.target.name]: event.target.value }));
    }
    const handleSubmit = () => {
        const palyload = {
            id: pack.id,
            packname: pack.packname,
            description:pack.description,
            cost:pack.cost
        }
        axios.put("http://localhost:9091/admin/washpack/update", palyload)
            .then(resp => alert(" id: " +resp.data.id +" updated successfully........"));
        navigate(-1);
    }
    return (
        <div  style={{marginTop:"8%"}}>
            {/* <NavBarAdmin/> */}

            <div  style={{fontSize:"20px"}}className="mrginlistalltrainer"> <a class="previous">&laquo;</a>
      <Link to="/washer"  style={{color:"chocolate"}} className="previous round"><u>Back</u></Link> </div>
             <h2 style={{color:"green"}}><u>Details</u></h2>
            <div>
                <label> ID:</label>
                <input type="text" name="corseId" value={pack.id} disabled />
            </div>
            <div>
                <label>PackName:</label>
                <input type="text" name="corseName" value={pack.packname} onChange={handleChange} />
            </div>

            <div>
                <label>Description:</label>
                <input type="text" name="courseAmount" value={pack.description} onChange={handleChange} />
            </div>
            <div>
                <label>Cost:</label>
                <input type="text" name="hours" value={pack.cost} onChange={handleChange} />
            </div>

            <div>
                <button className = "btn btn-primary mb-2" onClick={handleSubmit}>Update</button>
            </div>
        </div>
    )
}
export default  UpdatePack;
