import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router';
import {Link} from 'react-router-dom';
import "./CreateMovie.css";
import { Navbar } from 'react-bootstrap';


function Login() {
    const [loginForm, setLoginForm] = useState({
        username: "",
        password: ""
    })
    const [role1, setRole] = useState("");
    const [formErrors, setFormErrors] = useState({});
    const navigate = useNavigate();
    const handleChange = (event) => {
        setLoginForm(loginForm => ({ ...loginForm, [event.target.name]: event.target.value }));
    }
    const handleSubmit = (event) => {
        //validation login form
        let errors = {};
        if (!loginForm.username) {
            errors["usernameErr"] = "Username is required"
        }
        if (!loginForm.password) {
            errors["passwordErr"] = "Password is required"
        }
        if (!role1) {
            errors["roleErr"] = "Role is required"
        }
        setFormErrors(errors);
        const noErrors = Object.keys(errors).length === 0;
        // if no errors call the login api
        if (noErrors) {



            let payload = {
                username: loginForm.username,
                password: loginForm.password,
                role: role1
            }
            axios.post("http://localhost:8085/auth/login", payload).then(resp => {
                console.log(resp.data);
                alert("Welcome " + resp.data.role);
                let user = {
                    customerId: resp.data.customerId,
                    customerName: resp.data.customerName,
                    role: resp.data.role
                }
                localStorage.setItem("myUser", JSON.stringify(user));



                if (resp.data.role === "customer") {
                    navigate("/CustomerMenuBar");



                }
                if (resp.data.role === "admin") {



                    navigate("/AdminMenuBar");



                }



            }).catch(error => {
                console.log(error.response);
                alert("Invalid Credentials");
            })
        }
        event.preventDefault();
    }
    return (
       
        <div className="uday d-flex justify-content-center align-items-center pt-3 mt-0"  >
        <div className="container-fluid " style={{width:"700px",marginTop:"10px",paddingBottom:"20px",background:"rgba(0,0,0,0.1)"}}>
            <h2  style={{paddingTop:"20px",color:"white"}}><strong>LOGIN</strong></h2>

        <div class="row h-100 justify-content-center align-items-center ">
            
            <div className='col-10 col-md-8 col-lg-6'>
                <div className='form-group'>
                    <label htmlFor='username' style={{float:"left",color:"white"}}>Username:</label>
                    <input type="text" name="username" id="username" className="form-control" value={loginForm.username} onChange={handleChange} />
                    {
                        formErrors.usernameErr && <div style={{ color: "red", paddingBottom: 10,fontWeight:"600"}}>{formErrors.usernameErr}</div>
                    }
                </div>
                <div className='form-group'>
                    <label htmlFor='password' style={{float:"left",color:"white"}}>Password:</label>
                    <input type="password" name="password" id="password" className="form-control" value={loginForm.password} onChange={handleChange} />
                    {
                        formErrors.passwordErr && <div style={{ color: "red", paddingBottom: 10,fontWeight:"600"}}>{formErrors.passwordErr}</div>
                    }
                </div>
                <div>
                    {/* <input type="radio" id="huey" name="role" value="Customer" checked /> */}



                    {/* <input type="radio" value="customer" name="role1" checked/> Customer
<input type="radio" value="admin" name="role1" /> Admin */}



                    <input type="radio" id="role1" name="role1" value="customer" onChange={(e) => setRole("customer")}
                        checked={role1 === "customer"} /> <span style={{color:"white"}}>Customer</span> &nbsp; &nbsp; &nbsp;

                    <input type="radio"  id="role1" name="role1" value="admin" onChange={(e) => setRole("admin")}
                        checked={role1 === "admin"}  /> <span style={{color:"white"}}>Admin</span>
                    {
                        formErrors.roleErr && <div style={{ color: "red", paddingBottom: 10,fontWeight:"600" }}>{formErrors.roleErr}</div>
                    }
                </div>




                <div style={{paddingTop:"30px"}}>
                    <button onClick={handleSubmit} className="btn btn-success">Login</button> &nbsp;  &nbsp; 
                     <Link to="/createCustomer" ><button className="btn btn-info"> Register</button></Link> 
                </div>
            </div>
            </div>
        </div>
         </div>
        




    )
}
export default Login;
