import React, { useEffect, useState } from 'react';
import axios from "axios";
import {useParams} from 'react-router';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router';
//import "./CreateMovie.css";
//import Navbar from '../util/navbar';




function UpdatePack(){
    const[pack,setPack]=useState({
        cost:"",
        description: "",
        id:"",
        packname: "",
       
       
        
    });
    const navigate = useNavigate();
    const [formErrors, setFormErrors] = useState({});
    const {id} = useParams();

    const fetchpackById=()=>{
        axios.get("http://localhost:9091/admin/viewPack/"+id).then(resp=>setPack(resp.data))
    }
        useEffect(fetchpackById,[]);

        const handleChange=(event)=>{
            setPack( pack =>({...pack,[event.target.name]:event.target.value}));
    
        }
    
    
        const handleSubmit=()=>{
            let errors = {};

            if (!pack.id) {
                errors['movieNameErr'] = "Movie Name is Required";
            }
            if (!pack.packname) {
                errors['movieDurationErr'] = "Movie Duration is Required";
            }
             if (!pack.description) {
                errors['movieDurationErr'] = "Movie Duration is Required";
    
            }
            if (!pack.cost) {
                errors['movieGenreErr'] = "Movie Genre is Required";
            }
            
            
            setFormErrors(errors);
            const noErrors = Object.keys(errors).length === 0;
            if (noErrors)  {



            
            const payload={
                id:pack.id,
                packname:pack.packname,
                description:pack.description,
                cost:pack.cost
                
            }
           // navigate('/movies')
            axios.put("http://localhost:9091/admin/washpack/update",payload).then(resp=>alert("Movie updated successfully"));}
        }
       
            return(
                <div>
{/* <Navbar/> */}
                 <div className=" d-flex justify-content-center align-items-center pt-3 mt-0" style={{paddingBottom:"10px"}} >
            <div className="card w-25" style={{background:"rgba(0,0,0,0.3)"}}>
                {/* <h2>ADD MOVIE</h2> */}
                <div className="card-header " style={{backgroundColor:"#d3d3d3"}}>
                   <h3> UPDATE PACK</h3>
                </div>
                <div className="card-body">
                <div className="container">

                    <div className="row">


                    <div className="col-12 mb-3" >
                    <label style={{float:"left",margin:"0px",color:"white"}}>ID:</label>
                    <input type="number" className="form-control"  name="movieId" value={pack.id}  style={{fontWeight:"700" }} disabled />
                </div>
                <div className="col-12 mb-3">
                    <label style={{float:"left",margin:"0px",color:"white"}} >PACKNAME:</label>
                    <input type="text" className="form-control"  name="title" value={pack.packname} onChange={handleChange}  style={{fontWeight:"700" }} />
                    {
                formErrors.movieNameErr && <div style={{ color: "red", paddingBottom: 10 }}>{formErrors.movieNameErr}</div>
            }
                </div>
                <div className="col-12 mb-3">
                    <label style={{float:"left",margin:"0px",color:"white"}}>DESCRIPTION:</label>
                    <input type="number" className="form-control"  name="duration" value={pack.description} onChange={handleChange}  style={{fontWeight:"700" }}/>
                    {
                formErrors.movieDurationErr && <div style={{ color: "red", paddingBottom: 10 }}>{formErrors.movieDurationErr}</div>
            }
                </div>
                <div className="col-12 mb-3">
                    <label style={{float:"left",margin:"0px",color:"white"}}>COST:</label>
                    <input type="text" className="form-control"  name="genre" value={pack.cost} onChange={handleChange}  style={{fontWeight:"700" }}/>
                    {
                formErrors.movieGenreErr && <div style={{ color: "red", paddingBottom: 10 }}>{formErrors.movieGenreErr}</div>

            }
                </div>
                
                {/* </div> */}
                




        
                <div className="col-12 d-flex justify-content-around">
                    <button onClick={handleSubmit} className="btn btn-success" >update</button>
                </div>
                {/* <p>
                <Link to ="/movies">Click here to get Movie List</Link></p> */}
            </div>
            </div>
            </div>
            </div>
            </div>
            </div>
            )
        }
        





export default UpdatePack;
