import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router';
import { useNavigate } from 'react-router-dom';
// import { baseUrl } from '../util/AppConstants';
function DeletePack() {
    
    const [pack, setPack] = useState(null)
    const { id } = useParams();
    const navigate = useNavigate();
    const fetchPackById = () => {
        axios.get("http://localhost:8087/admin/viewPack/"+id).then(resp => setPack(resp.data))
    }
    useEffect(fetchPackById);
    const deleteWashPack = () => {
        axios.delete("http://localhost:9091/admin/washpack/delete/" + id).then(resp => {
            // fetch('http://localhost:9091/admin/washpack/delete/' + id, {
            //                 mode: 'no-cors',
                            
            //                 method: "Delete",
            //                 headers: {
            //                      "Content-Type": "application/json"
            //                 },
            //                 body: JSON.stringify()
            //      })



           


            alert(resp.data);
           
        })
      




    }
    return (
        <div>
            <h2>WashPack Details</h2>
            {
                pack !== null &&
                <div >
                    
                    <p> ID : {pack.id}</p>
                    <p> PACKNAME : {pack.packname}</p>
                    <p> DESCRIPTION : {pack.description}</p>
                    <p> COST : {pack.cost}</p>
                   





                </div>
            }
            <div><button onClick={deleteWashPack}>Delete</button></div>
        </div>
    )
}
export default DeletePack;
