import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Table } from "react-bootstrap";
import Footer from "./Footer";
import {card} from "react-bootstrap"


export default class Profile extends Component { // Start
  // Constructor
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }

  // ComponentDidMount is used to
  // execute the code
  componentDidMount() {
    fetch("http://localhost:9093/user/allusers")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        });
      });
  }

  //End

  render() {
    const { DataisLoaded, items } = this.state;

    return (
      
      <>
      <Card style={{ width: '18rem' }}>
  <CardBody>
    <CardTitle>Card title</CardTitle>
    <CardSubtitle className="mb-2 text-medium-emphasis">Card subtitle</CardSubtitle>
    <CardText>
      Some quick example text to build on the card title and make up the bulk of the card's content.
    </CardText>
    <CardLink href="#">Card link</CardLink>
    <CardLink href="#">Another link</CardLink>
  </CardBody>
</Card>
        <div className="text-centre">
          <h3 style={{marginLeft:"550px"}}>
            User Profiles
          </h3>
          
          
        </div>
        {items.map((item) => (
          <div className="container">
            <div className="row ">
              <div className="card" style={{width:"250px",marginBottom:"40px"}}>
                <div className="card-header text-centre">
                <label><b>{item.name}</b></label>
                

                </div>
                <div className="card-body"  >
                <label>Id: {item.id}</label><br/>
                <label>UserName: {item.username}</label><br/>
                <label>Contact: {item.contactno}</label><br/>
                <label>Email: {item.email}</label><br/>
                

                </div>

              </div>



            </div>

           
           
          </div>
        ))}

        <div>
          <Footer />
        </div>
      </>
    );
 }
}
