import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./login.css";
import Signup from "./SignUp";
import Navbar from "../Navbar";

// import { createButton } from "react-social-login-buttons";

const config = {
  text: "Log in with Facebook",
  icon: "facebook",
  iconFormat: (name) => `fa fa-${name}`,
  style: { background: "#3b5998" },
  activeStyle: { background: "#293e69" },
};
/** My Facebook login button. */
// const MyFacebookLoginButton = createButton(config);

const config1 = {
  activeStyle: { background: "#EFF0EE" },
  icon: "google",
  style: { background: "white", color: "black" },
  text: "Log in with Google",
};

// const GoogleLoginButton = createButton(config1);

const Login = () => {
  let navigate = useNavigate();
  const [name, setName] = useState("");
  const [pass, setPss] = useState("");
  const [nameErr, setNameErr] = useState(false);
  const [passErr, setpassErr] = useState(false);

  const handleName = (e) => {
    setName(e.target.value);
    setNameErr(false);
  };
  const handlePass = (e) => {
    setPss(e.target.value);
    setpassErr(false);
  };
  const handleClick = () => {
    debugger
    if (name && pass) {
      const data = {
        username: name,
        password: pass,
      };

      {
        axios
          .post("http://localhost:9093/user/login", data)
          .then(function (response) {
            if (response && response.data) {
              if (response.data === "logged in") {
                alert("Welcome " + response.data.id);
                navigate("/Userhome");
              } else {
                alert("wrong credentials.");
              }
            }
          })
          .catch(function (error) {
            console.log(error);
          });
      }
    } if (name) {
      setpassErr(true);
      setNameErr(false);
    } else if (pass) {
      setNameErr(true);
      setpassErr(false);
    } else {
      setpassErr(true);
      setNameErr(true);
    }
  };

  return (
    <div className="o" >
      <Navbar />
      <h1 className="title1a" ></h1>
      <div className="loginForm ">
        <h3 className="text-center">LOGIN</h3>
        <div className="name">

          <label>Username</label>
          <input
            type="text"
            placeholder="  Enter Username"
            value={name}
            onChange={handleName}
          />

          <br></br>
          {nameErr && <span>Enter valid name</span>}
          <br />
          <label>Password</label>

          <input
            type="password"
            placeholder="password"
            value={pass}
            onChange={handlePass}
          />
          <br></br>
          {passErr && <span>Enter valid password</span>}
        </div>
        <br />


        <div className="col-12 d-flex justify-content-around">
          <button onClick={handleClick} className="btn  mr-2" style={{ width:"100px",borderRadius:"25px", fontSize: "15px",color:"white",fontWeight:"700",background: "-webkit-linear-gradient(right,#00dbde,#fc00ff,#00dbde,#fc00ff)" }}>SignIn</button>
          <button className="btn btn-danger" style={{ fontSize: "15px" }} onClick={() => { window.location.href = "/Signup" }}>SignUp</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
