import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";
import { Dropdown, DropdownButton } from "react-bootstrap";
import UserNavbar from "./UserNavbar";


toast.configure();
const Booking = () => {
  // Start
  const [washers, setWashers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:9093/user/washpack").then((result) => {
      result.json().then((resp) => {
        setWashers(resp);
        console.log(resp);
      });
    });
  }, []);


  let navigate = useNavigate();

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    washpackId: "",
    address: "",
  });

  const changeHandler = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };
  const handleSelect = (e) => {
    console.log(e);
  }



  const submitHandler = () => {
    axios
      .post("http://localhost:9092/order/addorder", order)
      .then((res) => {
        if (res.status === 200) alert("Booked Successfully");
        else Promise.reject();
      })
      .catch((err) => alert("Something went wrong"));
  };

  return (
    <>
      <UserNavbar />
      {/* <div className="formTop"> */}
      {/* <div className="formTitle"> */}
      {/* <p className="c1 text-center">
            <h3>BOOK YOUR CAR WASH </h3>
          </p> */}
      {/* </div> */}
      {/* </div> */}
      <div className="row">

        <div className="formDetails col-md-6" style={{ padding: "0px" }}>
          <div className="text-center">
            <p className="formName" style={{ backgroundColor: "grey" }}>Book Your Order</p>
          </div>
          <form className="actualForm" onSubmit={submitHandler}>
            {/* <div >
              <p className="formName">Book Your Order</p>
            </div> */}

            {/* <div>
            {washers.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={changeHandler} /> {emp.cost}
              </div>
            ))}
            </div> */}


            <div >
              <input
                className="contactDetails"
                type="text"
                name="username"
                onChange={changeHandler}
                placeholder="Enter Your Full Name"
              />

              <input
                className="contactDetails"
                name="carName"
                type="text"

                placeholder="Enter Your Car Name"
                onChange={changeHandler}
              />


              <input


                className="contactDetails"
                type="text"
                name="carModel"
                placeholder="Enter Your Car Model"
                onChange={changeHandler}
              />

              <input
                className="formDateTime"
                type="date"
                name="date"
                onChange={changeHandler} />



              <input






                type="text"
                className="contactDetails"
                name="contactno"
                onChange={changeHandler}
                placeholder="Enter Contact no"
              />

              <input
                className="contactDetails"
                name="washpack"
               
                type="number"
                placeholder="Enter Your Washpack Id"

                onChange={changeHandler}
              />
              {/* <select name="Washpack" id="cars" onChange={changeHandler => this.target.value} >
                console.log(changeHandler)
                console.log(this.target.value)
                {washers.map((emp, ind) => (



                  <option value="emp.id" onChange={changeHandler}>{emp.id}</option>
                  



                ))}
              </select> */}







              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="5"
                type="text"
                name="address"
                onChange={changeHandler}
                placeholder="Your Address"
              />
            </div>

            <button onClick={submitHandler} className="btn btn-success mr-2" style={{ fontSize: "15px" }}>Submit</button>
          </form>
        </div>

        {/* <div className="col-md-6" >
          <h3>WashPacks</h3>

          <select name="Washpack" id="cars">
            {washers.map((emp, ind) => (



              <option value="emp.id">{emp.id}</option>



            ))}
          </select>
        </div> */}







<div className="col-md-6" >
          <h3>WashPacks</h3>

        
            {washers.map((emp, ind) => (


           
            <p>  {emp.id} &nbsp;{emp.cost}</p>

            
              


            ))}
          
        </div>






