import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Table } from "react-bootstrap";
import Footer from "./Footer";

export default class Profile extends Component { 
  
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false,
    };
  }

  // ComponentDidMount is used to
  // execute the code
  componentDidMount() {
    fetch("http://localhost:9093/user/allusers")
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true,
        });
      });
  }

  //End

  render() {
    const { DataisLoaded, items } = this.state;

    return (
      <>
        <div>
          
          <Table striped bordered hover variant="dark">
            <thead>
              <tr className="text-centre">
                <th>Id</th>
                <th>Name</th>
                <th>Username</th>
                <th>Contact</th>
                <th>Email</th>
              </tr>
            </thead>
          </Table>
        </div>
        {items.map((item) => (
          <div>
            {/* <label>Username:{item.name}</label> */}
            <Table striped bordered hover variant="dark">
              <tbody>
                <tr className = "card">
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.username}</td>
                  <td>{item.contactno}</td>
                  <td>{item.email}</td>
                 
                </tr>
              </tbody>
            </Table>
          </div>
        ))}

        <div>
          <Footer />
        </div>
      </>
    );
  }
}





