import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router";
import "../styling/AllStyling.css";
import {Link} from "react-router-dom";

function AddWasher() {
  const [washer, setWasher] = useState({
        id: "",
        location: "",
        name: "",
        password: ""
  });

  const navigate=useNavigate();
  const [formErrors, setFormErrors] = useState({});

  const handleChange = (event) => {
    setWasher((washer) => ({
      ...washer,
      [event.target.name]: event.target.value,
    }));
  };

  const handleSubmit = () => {
    let errors = {};

    if (!washer.id) {
      errors["idErr"] = "Enter Car Id";
    }

    if (!washer.location) {
      errors["locationErr"] = "Enter customer Id";
    }

    if (!washer.name) {
      errors["nameErr"] = "Enter Dealer Id";
    }
    if (!washer.password) {
        errors["passwordErr"] = "Enter Incentive Amount";
      }

    setFormErrors(errors);

    const noErrors = Object.keys(errors).length === 0;

    if (noErrors) {
      const payload = {
        id: washer.id,
        location: washer.location,
        name: washer.name,
        password: washer.password,
      }

      axios
        .post("http://localhost:9094/washer/addwasher", payload)

       
        navigate(-1);
    }
  };
  return (
    <div className="book">
    <div align="center">
      <hr/>
      <h2 style={{ color: "blue" }}>
        <b>Please Enter The Following Details</b>
      </h2>

      <div>
        <label className="col-sm-2 col-form-label"> Id</label>

        <input type="number" name="id" value={washer.id} onChange={handleChange}/>

        {formErrors.idErr && (
          <div style={{ color: "red", paddingBottom: 10 }}> {formErrors.idErr}  </div>
        )}
      </div>

      <div>
        <label className="col-sm-2 col-form-label">Location</label>

        <input type="number" name="location" value={washer.location} onChange={handleChange} />

        {formErrors.locationErr && (
          <div style={{ color: "red", paddingBottom: 10 }}> {formErrors.locationErr}  </div>
        )}
      </div>

      <div>
        <label className="col-sm-2 col-form-label">Name</label>

        <input type="number" name="name" value={washer.name} onChange={handleChange} />

        {formErrors.nameErr && (
          <div style={{ color: "red", paddingBottom: 10 }}> {formErrors.nameErr}  </div>
        )}
      </div>

      <div>
        <label className="col-sm-2 col-form-label">Password</label>

        <input type="number" name="password" value={washer.password} onChange={handleChange} />

        {formErrors.passwordErr && (
          <div style={{ color: "red", paddingBottom: 10 }}> {formErrors.passwordErr}  </div>
        )}
      </div>

      <div>
        <button className="btn btn-success" style={{ color: "maroon" }} onClick={handleSubmit}>
          Save
        </button>
      </div>
    </div>
    </div>
  );
}
export default AddWasher;
