import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

import { useNavigate } from 'react-router-dom';


const Admin = () => {
    const [washers, setWashers] = useState([])

    let navigate = useNavigate()


    useEffect(() => {
        fetch("http://localhost:9091/admin/washpack").then(result => {
            result.json().then(
                resp => {
                    setWashers(resp)
                    console.log(resp)
                })
        })
    }, [])




    return (

        <div>
            <div>
                <h1 className="bookAppointTitle mb-5 text-center">WashPack Details</h1>
            </div>

            {washers.map((emp) => (
                // <div className="bk2 ml-5" >
                <div className='row'>
                    <div className='card' style={{width:"350px"}}>
                    <div >
                        <h4>Id : {emp.id}</h4>
                        <p > Name : {emp.packname}</p>
                        <p >Description : {emp.description}</p>
                        <p>Cost: {emp.cost}</p>
                       
                    </div>
                    <div>
                        
                        <button className=" bookBtn bookBtnUpdate" onClick={() => { navigate('/FetchAllPacks') }}>Update Pack</button>
                        <button className=" bookBtn bookBtnUpdate" onClick={() => { navigate(`/Details/${emp.id}`) }}>Delete Pack</button>
                        {/* <td><Link to ={`/Details/edit/${emp.id}`}className="btn btn-outline-secondary">EDIT</Link></td> */}
                    </div>
                    <hr style={{ marginTop: "30px" }} />
                </div>
                </div>
            ))
            }
        </div>

    )
}

export default Admin
