import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from 'react-router';

function UpdatePack() {
    const [pack, setpack] = useState({
        id: "",
        packname: "",
        description: "",
        cost: "",
     

    });

    const { id } = useParams();
    const navigate = useNavigate();

    const fetchPackById = () => {
        axios.get("http://localhost:9091/admin/viewPack/" + id).then(resp => setpack(resp.data))
    }

    useEffect(fetchPackById, []);

    const handleChange = (event) => {
        setpack(pack => ({ ...pack, [event.target.name]: event.target.value }));
    }
    const handleSubmit = () => {
        const payload = {
            id: pack.id,
            packname: pack.packname,
            description: pack.description,
            cost: pack.cost
           

        }
        axios.put("http://localhost:9091/admin/washpack/update", payload)
            .then(resp => alert("WashPack Details are updated successfully:" + resp.data.Id));

        navigate(-1);
    }

    return (
        <div className="App">
            <div class="alert alert-primary" role="alert">
                <p class="h2">UPDATE PACKS..!</p>
            </div>
            <div class="h4">
                <ul class="list-group">
                    <div class="list-group-item">
                        <label>ID</label>
                        <li class=""> </li>
                        <input type="number" name="id" value={pack.id} onChange={handleChange} />
                    </div>
                    <div class="list-group-item">
                        <label>PackName</label>
                        <li class=" "></li>
                        <input type="number" name="theaterId" value={pack.packname} onChange={handleChange} />
                    </div>
                    <div class="list-group-item">
                        <label>Description</label>
                        <li class=" "></li>
                        <input type="number" name="showId" value={pack.description} onChange={handleChange} />
                    </div>
                    <div class="list-group-item">
                        <label>Cost</label>
                        <li class=" "></li>
                        <input type="number" name="movieId" value={pack.cost} onChange={handleChange} />
                    </div>
                    
                </ul>
            </div>
            <div>
                <button onClick={handleSubmit}>UpdatePack</button>
            </div>
        </div>
    )

}
export default UpdatePack;
